    private void jButton_guardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_guardarActionPerformed
        //validar campos
        if (txt_titulo.getText().isEmpty() && txt_contenido.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "complete todo los campos");
            this.validar();
        } else {

            Nota nota = new Nota();
            Ctrl_Nota ctrl_Nota = new Ctrl_Nota();

            if (!ctrl_Nota.existeNota(txt_titulo.getText().trim())) {

                nota.setTitulo(txt_titulo.getText().trim());
                nota.setContenido(txt_contenido.getText().trim());
                nota.setFechaCreacion(txt_fechacreacion.getText().trim());
                nota.setFechaModificacion(txt_fechamodificacion.getText().trim());
                nota.setPrioridad(txt_prioridad.getText().trim());

                if (ctrl_Nota.guardar(nota)) {
                    JOptionPane.showMessageDialog(null, "Registro Guardado");
                    txt_titulo.setBackground(Color.green);
                    txt_contenido.setBackground(Color.green);
                    txt_fechacreacion.setBackground(Color.green);
                    txt_fechamodificacion.setBackground(Color.green);
                    txt_prioridad.setBackground(Color.green);
                } else {
                    JOptionPane.showMessageDialog(null, "Error al Guardar");
                }

            } else {
                JOptionPane.showMessageDialog(null, "El Cliente ya esta registrado en Base de Datos");
                txt_titulo.setBackground(Color.white);
                txt_contenido.setBackground(Color.white);
                txt_fechacreacion.setBackground(Color.white);
                txt_fechamodificacion.setBackground(Color.white);
                txt_prioridad.setBackground(Color.white);
            }
        }

        //metodo para limpiar
        this.Limpiar();

    }//GEN-LAST:event_jButton_guardarActionPerformed

