    //metodo de limpiar
    private void Limpiar() {
        txt_titulo.setText("");
        txt_contenido.setText("");
        txt_fechacreacion.setText("");
        txt_fechamodificacion.setText("");
        txt_prioridad.setText("");
    }

    public void validar() {
        if (txt_titulo.getText().isEmpty()) {
            jLab_titulo.setText("*campo obligatorio ");
        } else {
            jLab_titulo.setText("");
        }
        if (txt_contenido.getText().isEmpty()) {
            jLab_contenido.setText("*campo obligatorio");

        } else {
            jLab_contenido.setText("");
        }
        if (txt_fechacreacion.getText().isEmpty()) {
            jLab_fechac.setText("*completar campo");

        } else {
            jLab_fechac.setText("");

        }
        if (txt_fechamodificacion.getText().isEmpty()) {
            jLab_fecham.setText("*completar campo");
        } else {
            jLab_fecham.setText("");
        }
        if (txt_prioridad.getText().isEmpty()) {
            jLab_prioridad.setText("*completar campo");

        } else {
            jLab_prioridad.setText("");
        }

    }

